	primaryId, err := db.Query("ALTER TABLE `domains`  ADD PRIMARY KEY (`id`);")
	if err != nil {
		panic(err.Error())
	}
	defer primaryId.Close()

	//Retrieving the values from table
	retrieve, err := db.Query("SELECT id, domain FROM domains")
	if err != nil {
		panic(err.Error())
	}
	defer retrieve.Close()

	//Iterating over values retrieved
	for retrieve.Next() {
		wg.Add(1)
		go func() {
			defer wg.Done()
			var results Results
			err = retrieve.Scan(&results.id, &results.domain) //scanning the records
			if err != nil {
				panic(err.Error())
			}

			//storing the value from 'domain' field for particular record
			finalDomain := results.domain

			//Hitting the website URLs (domains) and storing their HTML webpages in files
			func() {
				//Hitting the website
				resp, err := http.Get("http://" + finalDomain)
				//Checking the invalid URLs
				if err != nil {
					fmt.Println(finalDomain + " not found")
					fmt.Println(err)
					defer func() {
						if r := recover(); err != nil {
							fmt.Println("recovered from ", r)
						}
					}()
				}
				defer resp.Body.Close()

				//Reading webpage of the domain and storing it
				body, err := ioutil.ReadAll(resp.Body)
				if err != nil {
					fmt.Println(err)
				}
				storeData := string(body)

				//Getting first letter of domain
				firstLetterOfDomain := string(finalDomain[0])

				//Creating files for every domain
				fileCreation, err := os.Create("/home/vishu/go/src/SQLProject/src/HTML-Codes/" + firstLetterOfDomain + "/" + finalDomain + ".html")
				if err != nil {
					fmt.Println(err)
				}

				//Writing webpage contents to file
				writingToFile, err := fileCreation.WriteString(storeData)
				if err != nil {
					fmt.Println(err)
				}
				fmt.Println(writingToFile)
			}()
		}()
		wg.Wait()
	}
